import React from "react";
import { useNavigate } from "react-router-dom";

const buttonStyle = {
  fontSize: "16px",
  backgroundColor: "#4BAFFF",
  color: "white",
  padding: "10px 20px",
  border: "none",
  borderRadius: "8px",
  margin: "10px",
  cursor: "pointer",
};

const buttonContainerStyle = {
  display: "grid",
  gridTemplateColumns: "repeat(auto-fill, minmax(150px, 1fr))",
  gap: "15px",
  justifyContent: "center",
  padding: "20px",
};

const CashierDashboard = () => {
  const navigate = useNavigate();

  const handleNavigation = (category) => {
    navigate(`/menu-category/${category}`);
  };

  return (
    <div style={{ textAlign: "center", backgroundColor: "#f0f0f0", minHeight: "100vh" }}>
      <img
        src="/logo192.png"
        alt="Logo"
        style={{ width: "120px", margin: "20px auto" }}
      />
      <h1>Cashier Page</h1>

      <div style={buttonContainerStyle}>
        <button style={buttonStyle} onClick={() => handleNavigation("Fruit Tea")}>Fruit Tea</button>
        <button style={buttonStyle} onClick={() => handleNavigation("Milk Tea")}>Milk Tea</button>
        <button style={buttonStyle} onClick={() => handleNavigation("Brewed Tea")}>Brewed Tea</button>
        <button style={buttonStyle} onClick={() => handleNavigation("Ice Blended")}>Ice Blended</button>
        <button style={buttonStyle} onClick={() => handleNavigation("Fresh Milk")}>Fresh Milk</button>
        <button style={buttonStyle} onClick={() => handleNavigation("Signature Blends")}>Signature Blends</button>
        <button style={buttonStyle} onClick={() => handleNavigation("Seasonal")}>Seasonal</button>
        <button style={buttonStyle} onClick={() => handleNavigation("Additional toppings")}>Additional Toppings</button>
        <button style={buttonStyle} onClick={() => navigate("/orders")}>Orders</button>
        <button style={buttonStyle} onClick={() => navigate("/")}>Logout</button>
      </div>
    </div>
  );
};

export default CashierDashboard;
