// src/components/OrderHistory.js
import React, { useEffect, useState } from "react";
import axios from "axios";

const OrderHistory = () => {
  const [transactions, setTransactions] = useState([]);

  useEffect(() => {
    fetchTransactions();
  }, []);

  const fetchTransactions = async () => {
    try {
      const res = await axios.get("http://localhost:5050/api/transactions");
      setTransactions(res.data);
    } catch (err) {
      console.error("‚ùå Failed to fetch transactions:", err);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>üìã Order History</h2>
      {transactions.length === 0 ? (
        <p>No transaction records found.</p>
      ) : (
        <table border="1" style={{ width: "100%", borderCollapse: "collapse" }}>
          <thead>
            <tr>
              <th>ID</th>
              <th>Sale ID</th>
              <th>Menu Item ID</th>
              <th>Quantity</th>
              <th>Price ($)</th>
              <th>Payment Method</th>
              <th>Transaction Category</th>
            </tr>
          </thead>
          <tbody>
            {transactions.map((txn) => (
              <tr key={txn.id}>
                <td>{txn.id}</td>
                <td>{txn.sale_id}</td>
                <td>{txn.menu_item_id}</td>
                <td>{txn.quantity}</td>
                <td>{parseFloat(txn.price).toFixed(2)}</td>
                <td>{txn.paymentmethod}</td>
                <td>{txn.transactioncategory}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default OrderHistory;
