import React, { useState } from 'react';
import axios from 'axios';

const ZReport = () => {
  const [startHour, setStartHour] = useState(0);
  const [endHour, setEndHour] = useState(23);
  const [reportData, setReportData] = useState([]);
  const [csvData, setCsvData] = useState('');
  const [loading, setLoading] = useState(false);

  // 获取 Z-Report 数据
  const fetchReportData = async () => {
    if (startHour > endHour) {
      alert('Start hour cannot be greater than end hour');
      return;
    }

    setLoading(true);
    try {
      const response = await axios.get(`http://localhost:5050/api/zreport?startHour=${startHour}&endHour=${endHour}`);

      setReportData(response.data);
      prepareCSVData(response.data);
    } catch (error) {
      console.error('❌ Error fetching Z-report data:', error);
      alert('⚠️ Failed to fetch Z-report data. Check the backend or console.');
    } finally {
      setLoading(false);
    }
  };

  // 准备 CSV 数据
  const prepareCSVData = (data) => {
    const headers = [
      "Hour", "Transactions", "Sales Revenue", "Total Sales",
      "Total Tax", "Service Charges", "Voids", "Discards",
      "Discounts", "Employee Signatures", "Payment Methods"
    ];
    const rows = data.map(row => [
      row.hour,
      row.total_transactions,
      Number(row.sales_revenue).toFixed(2),
      Number(row.total_sales).toFixed(2),
      Number(row.total_tax).toFixed(2),
      Number(row.service_charges).toFixed(2),
      Number(row.voids).toFixed(2),
      Number(row.discards).toFixed(2),
      Number(row.discounts).toFixed(2),
      `"${row.employee_signatures || 'N/A'}"`,
      `"${row.payment_methods || 'N/A'}"`
    ].join(','));

    const csv = [headers.join(','), ...rows].join('\n');
    setCsvData(csv);
  };

  // 导出 CSV
  const handleExportToCSV = () => {
    if (!csvData) {
      alert("No data to export");
      return;
    }

    const blob = new Blob([csvData], { type: 'text/csv' });
    const link = document.createElement('a');
    link.href = URL.createObjectURL(blob);
    link.download = 'Z_Report.csv';
    link.click();
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Z-Report - Hourly Sales Breakdown</h2>

      <div>
        <label>Start Hour: </label>
        <input
          type="number"
          value={startHour}
          onChange={e => setStartHour(parseInt(e.target.value))}
          min="0" max="23"
        />
      </div>

      <div>
        <label>End Hour: </label>
        <input
          type="number"
          value={endHour}
          onChange={e => setEndHour(parseInt(e.target.value))}
          min="0" max="23"
        />
      </div>

      <div style={{ margin: '10px 0' }}>
        <button onClick={fetchReportData}>Generate Report</button>
        <button onClick={handleExportToCSV} style={{ marginLeft: '10px' }}>
          Export to CSV
        </button>
      </div>

      {loading ? (
        <p>Loading...</p>
      ) : (
        <table border="1" style={{ width: '100%', borderCollapse: 'collapse' }}>
          <thead>
            <tr>
              <th>Hour</th>
              <th>Transactions</th>
              <th>Sales Revenue</th>
              <th>Total Sales</th>
              <th>Total Tax</th>
              <th>Service Charges</th>
              <th>Voids</th>
              <th>Discards</th>
              <th>Discounts</th>
              <th>Employee Signatures</th>
              <th>Payment Methods</th>
            </tr>
          </thead>
          <tbody>
            {reportData.length === 0 ? (
              <tr><td colSpan="11">No data available</td></tr>
            ) : (
              reportData.map((row, index) => (
                <tr key={index}>
                  <td>{row.hour}</td>
                  <td>{row.total_transactions}</td>
                  <td>{Number(row.sales_revenue).toFixed(2)}</td>
                  <td>{Number(row.total_sales).toFixed(2)}</td>
                  <td>{Number(row.total_tax).toFixed(2)}</td>
                  <td>{Number(row.service_charges).toFixed(2)}</td>
                  <td>{Number(row.voids).toFixed(2)}</td>
                  <td>{Number(row.discards).toFixed(2)}</td>
                  <td>{Number(row.discounts).toFixed(2)}</td>
                  <td>{row.employee_signatures || 'N/A'}</td>
                  <td>{row.payment_methods || 'N/A'}</td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default ZReport;
