import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ZReport = () => {
  const [startHour, setStartHour] = useState(0);
  const [endHour, setEndHour] = useState(23);
  const [reportData, setReportData] = useState([]);
  const [csvData, setCsvData] = useState('');

  // 获取报告数据
  const fetchReportData = async () => {
    try {
      const response = await axios.get(`/api/zreport?startHour=${startHour}&endHour=${endHour}`);
      setReportData(response.data);
      prepareCSVData(response.data);
    } catch (error) {
      alert('Error fetching Z-report data');
      console.error(error);
    }
  };

  // 准备CSV数据
  const prepareCSVData = (data) => {
    const headers = [
      "Hour", "Transactions", "Sales Revenue", "Total Sales", 
      "Total Tax", "Service Charges", "Voids", "Discards", 
      "Discounts", "Employee Signatures", "Payment Methods"
    ];

    const rows = data.map(row => {
      return [
        row.hour,
        row.total_transactions,
        row.sales_revenue,
        row.total_sales,
        row.total_tax,
        row.service_charges,
        row.voids,
        row.discards,
        row.discounts,
        row.employee_signatures,
        row.payment_methods
      ].map(value => `"${value}"`).join(',');
    });

    const csv = [headers.join(','), ...rows].join('\n');
    setCsvData(csv);
  };

  // 导出CSV文件
  const handleExportToCSV = () => {
    const blob = new Blob([csvData], { type: 'text/csv' });
    const link = document.createElement('a');
    link.href = URL.createObjectURL(blob);
    link.download = 'Z_Report.csv';
    link.click();
  };

  // 生成报告
  const handleGenerateReport = () => {
    if (parseInt(startHour) > parseInt(endHour)) {
      alert('Start hour cannot be greater than end hour');
      return;
    }
    fetchReportData();
  };

  // 限制开始小时和结束小时的输入
  const handleStartHourChange = (e) => {
    let value = parseInt(e.target.value);
    if (value < 0) value = 0;
    if (value > 23) value = 23;
    setStartHour(value);
  };

  const handleEndHourChange = (e) => {
    let value = parseInt(e.target.value);
    if (value < 0) value = 0;
    if (value > 23) value = 23;
    setEndHour(value);
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Z-Report - Hourly Sales Breakdown</h2>

      <div>
        <label>Start Hour:</label>
        <input 
          type="number" 
          value={startHour} 
          onChange={handleStartHourChange} 
          min="0" 
          max="23" 
        />
      </div>
      <div>
        <label>End Hour:</label>
        <input 
          type="number" 
          value={endHour} 
          onChange={handleEndHourChange} 
          min="0" 
          max="23" 
        />
      </div>
      <div>
        <button onClick={handleGenerateReport}>Generate Report</button>
        <button onClick={handleExportToCSV}>Export to CSV</button>
      </div>

      <table border="1" style={{ marginTop: '20px', width: '100%' }}>
        <thead>
          <tr>
            <th>Hour</th>
            <th>Transactions</th>
            <th>Sales Revenue</th>
            <th>Total Sales</th>
            <th>Total Tax</th>
            <th>Service Charges</th>
            <th>Voids</th>
            <th>Discards</th>
            <th>Discounts</th>
            <th>Employee Signatures</th>
            <th>Payment Methods</th>
          </tr>
        </thead>
        <tbody>
          {reportData.length === 0 ? (
            <tr>
              <td colSpan="11">No data available</td>
            </tr>
          ) : (
            reportData.map((row, index) => (
              <tr key={index}>
                <td>{row.hour}</td>
                <td>{row.total_transactions}</td>
                <td>{row.sales_revenue}</td>
                <td>{row.total_sales}</td>
                <td>{row.total_tax}</td>
                <td>{row.service_charges}</td>
                <td>{row.voids}</td>
                <td>{row.discards}</td>
                <td>{row.discounts}</td>
                <td>{row.employee_signatures}</td>
                <td>{row.payment_methods}</td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
};

export default ZReport;
