// components/PaymentPage.js
import React from "react";
import { useNavigate, useLocation } from "react-router-dom";

const PaymentPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const orderTotal = location.state?.total || 0;

  const handleCash = () => {
    navigate("/payment/cash", { state: { total: orderTotal } });
  };

  const handleCard = () => {
    alert("ðŸ’³ Payment successful via Card!");
    navigate("/thankyou", { state: { total: orderTotal } });
  };

  return (
    <div style={{ padding: "40px", textAlign: "center" }}>
      <h2>ðŸ’° Payment Page</h2>
      <p>Total Amount: ${orderTotal.toFixed(2)}</p>
      <button onClick={handleCash}>Pay with Cash</button>
      <button onClick={handleCard} style={{ marginLeft: "20px" }}>
        Pay with Card
      </button>
    </div>
  );
};

export default PaymentPage;
