import React, { useEffect, useState } from "react";
import axios from "axios";
import { Bar } from "react-chartjs-2";
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from "chart.js";
import { useNavigate } from "react-router-dom";

// 注册 Chart.js 组件
ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const ProductUsage = () => {
  const [salesData, setSalesData] = useState([]);
  const [dateLabels, setDateLabels] = useState([]);
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");
  const navigate = useNavigate();

  // 获取数据并更新图表
  useEffect(() => {
    if (startDate && endDate) {
      loadTrendData();
    }
  }, [startDate, endDate]);

  const loadTrendData = async () => {
    try {
      const response = await axios.get("http://localhost:5050/api/productusage", {
        params: { startDate, endDate },
      });
      const trendData = response.data;
      setSalesData(trendData.map((item) => item.totalUsed));
      setDateLabels(trendData.map((item) => item.itemName));
    } catch (err) {
      console.error("Error fetching product usage data:", err);
      alert("❌ Failed to fetch trend data.");
    }
  };

  const chartData = {
    labels: dateLabels, // X 轴：产品名称
    datasets: [
      {
        label: "Total Usage",
        data: salesData, // Y 轴：产品使用数量
        backgroundColor: "rgba(75, 192, 192, 0.2)", // 条形的背景色
        borderColor: "rgba(75, 192, 192, 1)", // 条形的边框颜色
        borderWidth: 1,
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    scales: {
      x: {
        title: {
          display: true,
          text: "Product Name",
        },
      },
      y: {
        title: {
          display: true,
          text: "Total Usage",
        },
      },
    },
  };

  const handleBack = () => {
    navigate(-1); // 返回上一页
  };

  return (
    <div style={{ padding: "20px" }}>
      <button onClick={handleBack} style={{ marginBottom: "10px" }}>
        🔙 Back
      </button>

      <h2>📊 Product Usage Report</h2>

      <div>
        <label>
          Start Date:
          <input
            type="date"
            value={startDate}
            onChange={(e) => setStartDate(e.target.value)}
          />
        </label>

        <label>
          End Date:
          <input
            type="date"
            value={endDate}
            onChange={(e) => setEndDate(e.target.value)}
          />
        </label>
      </div>

      <div>
        <h3>Bar Chart of Product Usage</h3>
        <Bar data={chartData} options={chartOptions} />
      </div>
    </div>
  );
};

export default ProductUsage;
