// ProductUsage.js (Web version of ShowProductUsage)
import React, { useState } from 'react';
import axios from 'axios';

const ProductUsage = () => {
  const [data, setData] = useState([]);
  const [timeRange, setTimeRange] = useState("Today");
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");

  const fetchData = async () => {
    try {
      const params = { timeRange, startDate, endDate };
      const res = await axios.get("/api/product-usage", { params });
      setData(res.data);
    } catch (err) {
      alert("Error fetching usage data");
    }
  };

  const handleApply = () => {
    fetchData();
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>ðŸ“¦ Product Usage</h2>

      <div>
        <select value={timeRange} onChange={(e) => setTimeRange(e.target.value)}>
          <option value="Today">Today</option>
          <option value="Last 7 Days">Last 7 Days</option>
          <option value="Last 30 Days">Last 30 Days</option>
          <option value="Custom Range">Custom Range</option>
        </select>
        {timeRange === "Custom Range" && (
          <>
            <input
              type="text"
              placeholder="Start Date (YYYY-MM-DD)"
              value={startDate}
              onChange={(e) => setStartDate(e.target.value)}
            />
            <input
              type="text"
              placeholder="End Date (YYYY-MM-DD)"
              value={endDate}
              onChange={(e) => setEndDate(e.target.value)}
            />
          </>
        )}
        <button onClick={handleApply}>Apply</button>
      </div>

      <ul>
        {data.map((entry, idx) => (
          <li key={idx}>
            {entry.itemname}: {entry.total_used}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ProductUsage;
