import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Trends = () => {
  const [trendData, setTrendData] = useState([]);
  const [trendType, setTrendType] = useState("Daily Sales");
  const [timeRange, setTimeRange] = useState("Last 7 Days");
  const navigate = useNavigate();

  useEffect(() => {
    fetchTrendData();
  }, [trendType, timeRange]);

  const fetchTrendData = async () => {
    try {
      const res = await axios.get("http://localhost:5050/api/trends", {
        params: {
          trendType,
          timeRange,
        },
      });
      setTrendData(res.data);
    } catch (err) {
      alert("‚ùå Failed to fetch trends");
    }
  };

  const handleTrendTypeChange = (e) => {
    setTrendType(e.target.value);
  };

  const handleTimeRangeChange = (e) => {
    setTimeRange(e.target.value);
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>üìä Trends Report</h2>

      <div>
        <select onChange={handleTrendTypeChange} value={trendType}>
          <option value="Daily Sales">Daily Sales</option>
          <option value="Peak Days">Peak Days</option>
          <option value="Total Orders">Total Orders</option>
          <option value="Product Usage">Product Usage</option>
        </select>

        <select onChange={handleTimeRangeChange} value={timeRange}>
          <option value="Last 7 Days">Last 7 Days</option>
          <option value="Last 30 Days">Last 30 Days</option>
          <option value="All Time">All Time</option>
        </select>
      </div>

      <div>
        <h3>{trendType} ({timeRange})</h3>
        <table border="1" style={{ width: "100%" }}>
          <thead>
            <tr>
              <th>Date</th>
              <th>Value</th>
            </tr>
          </thead>
          <tbody>
            {trendData.map((data, index) => (
              <tr key={index}>
                <td>{data.date}</td>
                <td>{data.value}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <button style={{ marginTop: "20px" }} onClick={() => navigate("/manager")}>
        ‚¨ÖÔ∏è Back to Manager
      </button>
    </div>
  );
};

export default Trends;
