import React, { useState, useEffect } from "react";
import axios from "axios";
import { Line } from "react-chartjs-2";
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from "chart.js";
import { useNavigate } from "react-router-dom";

// Ê≥®ÂÜåChart.jsÁªÑ‰ª∂
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

const ProductUsage = () => {
  const [salesData, setSalesData] = useState([]);
  const [dateLabels, setDateLabels] = useState([]);
  const [currentTrend, setCurrentTrend] = useState("Product Usage");
  const [timeRange, setTimeRange] = useState("Last 7 Days");
  const navigate = useNavigate(); // ‚úÖ ÂàùÂßãÂåñ navigate

  // Ëé∑ÂèñË∂ãÂäøÊï∞ÊçÆ
  useEffect(() => {
    const loadTrendData = async (trendType) => {
      try {
        const response = await axios.get("http://localhost:5050/api/trends", {
          params: {
            trendType: trendType,
            timeRange: timeRange,
          },
        });
        const trendData = response.data;
        setSalesData(trendData.map((item) => item.value));
        setDateLabels(trendData.map((item) => item.date));
      } catch (err) {
        console.error("Error fetching trend data:", err);
        alert("‚ùå Failed to fetch trend data.");
      }
    };

    loadTrendData(currentTrend);
  }, [currentTrend, timeRange]); // ÂåÖÊã¨ loadTrendData Âíå‰æùËµñÈ°π

  const handleTrendChange = (trendType) => {
    setCurrentTrend(trendType);
  };

  const handleTimeRangeChange = (event) => {
    setTimeRange(event.target.value);
  };

  // ÈÖçÁΩÆÂõæË°®ÁöÑÊï∞ÊçÆÂíåÈÄâÈ°π
  const chartData = {
    labels: dateLabels, // xËΩ¥ÔºöÊó•Êúü
    datasets: [
      {
        label: currentTrend,
        data: salesData, // yËΩ¥ÔºöË∂ãÂäøÊï∞ÊçÆ
        fill: false,
        borderColor: "rgba(75, 192, 192, 1)", // ËÆæÁΩÆÊäòÁ∫øÈ¢úËâ≤
        tension: 0.1,
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    scales: {
      x: {
        title: {
          display: true,
          text: "Date",
        },
      },
      y: {
        title: {
          display: true,
          text: "Value",
        },
      },
    },
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>üìä Product Usage Report</h2>

      <div>
        <h3>Select Trend Type</h3>
        <button onClick={() => handleTrendChange("Product Usage")}>Product Usage</button>
      </div>

      <div>
        <h3>Select Time Range</h3>
        <select value={timeRange} onChange={handleTimeRangeChange}>
          <option value="Last 7 Days">Last 7 Days</option>
          <option value="Last 30 Days">Last 30 Days</option>
          <option value="All Time">All Time</option>
        </select>
      </div>

      <div>
        <h3>Data Chart</h3>
        <Line data={chartData} options={chartOptions} />
      </div>

      <div>
        <h3>Trend Data</h3>
        <table border="1" style={{ width: "100%" }}>
          <thead>
            <tr>
              <th>Date</th>
              <th>Value</th>
            </tr>
          </thead>
          <tbody>
            {dateLabels.map((date, index) => (
              <tr key={index}>
                <td>{date}</td>
                <td>{salesData[index]}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <button style={{ marginTop: "20px" }} onClick={() => navigate("/")}>
        ‚¨ÖÔ∏è Back to Manager
      </button>
    </div>
  );
};

export default ProductUsage;
