import React, { useEffect, useState } from "react";
import axios from "axios";

const DrinkMenu = () => {
  const [drinkName, setDrinkName] = useState("");
  const [category, setCategory] = useState("");
  const [price, setPrice] = useState("");
  const [menuList, setMenuList] = useState([]);

  const viewMenu = async () => {
    try {
      const res = await axios.get("http://localhost:5050/api/menu");
      setMenuList(res.data);
    } catch (err) {
      alert("‚ùå Failed to fetch menu.");
    }
  };

  const addDrink = async () => {
    if (!drinkName || !category || !price) {
      alert("Please fill out all fields.");
      return;
    }
    try {
      await axios.post("http://localhost:5050/api/menu/add", {
        itemname: drinkName,
        itemtype: category,
        itemprice: parseFloat(price),
      });
      alert("‚úÖ Drink added successfully!");
      setDrinkName("");
      setCategory("");
      setPrice("");
      viewMenu();
    } catch (err) {
      alert("‚ùå Failed to add drink.");
    }
  };

  const deleteDrink = async () => {
    if (!drinkName) {
      alert("Please enter the drink name to delete.");
      return;
    }
    try {
      await axios.post("http://localhost:5050/api/menu/delete", {
        itemname: drinkName,
      });
      alert("üóëÔ∏è Drink deleted successfully!");
      viewMenu();
    } catch (err) {
      alert("‚ùå Failed to delete drink.");
    }
  };

  useEffect(() => {
    viewMenu();
  }, []);

  return (
    <div style={{ padding: "20px" }}>
      <h2>ü•§ Drink Menu Management</h2>
      <div>
        <input
          type="text"
          placeholder="Drink Name"
          value={drinkName}
          onChange={(e) => setDrinkName(e.target.value)}
        />
        <input
          type="text"
          placeholder="Category"
          value={category}
          onChange={(e) => setCategory(e.target.value)}
        />
        <input
          type="number"
          placeholder="Price"
          value={price}
          onChange={(e) => setPrice(e.target.value)}
        />
        <div style={{ marginTop: "10px" }}>
          <button onClick={addDrink}>‚ûï Add Drink</button>
          <button onClick={deleteDrink}>üóëÔ∏è Delete Drink</button>
          <button onClick={viewMenu}>üîÑ View Menu</button>
        </div>
      </div>

      <div style={{ marginTop: "30px" }}>
        <h3>üìã Available Drinks</h3>
        <ul>
          {menuList.map((item) => (
            <li key={item.itemid}>
              {item.itemname} ({item.itemtype}) - ${parseFloat(item.itemprice).toFixed(2)}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default DrinkMenu;
