import React, { useState, useEffect } from "react";
import axios from "axios";
import { Line } from 'react-chartjs-2'; // ‰ΩøÁî® chart.js ÁªòÂà∂ÂõæË°®
import { Chart as ChartJS, Title, Tooltip, Legend, LineElement, CategoryScale, LinearScale } from 'chart.js';

ChartJS.register(Title, Tooltip, Legend, LineElement, CategoryScale, LinearScale);

const Trends = () => {
  const [trendData, setTrendData] = useState([]);
  const [selectedTrend, setSelectedTrend] = useState("Daily Sales");
  const [timeRange, setTimeRange] = useState("Last 7 Days");

  useEffect(() => {
    fetchTrendData();
  }, [selectedTrend, timeRange]);

  // Ëé∑ÂèñË∂ãÂäøÊï∞ÊçÆ
  const fetchTrendData = async () => {
    try {
      const res = await axios.get("http://localhost:5050/api/trends", {
        params: { trendType: selectedTrend, timeRange: timeRange }
      });
      setTrendData(res.data);
    } catch (err) {
      console.error("Error fetching trend data:", err);
    }
  };

  // ÁîüÊàêÂõæË°®Êï∞ÊçÆ
  const chartData = {
    labels: trendData.map(item => item.date),  // Êó•Êúü
    datasets: [{
      label: selectedTrend,
      data: trendData.map(item => item.value),  // Êï∞ÊçÆÂÄº
      borderColor: "rgba(75, 192, 192, 1)",
      tension: 0.1
    }]
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>üìä Trends Report</h2>

      <div>
        <label>Choose Trend Type:</label>
        <select onChange={(e) => setSelectedTrend(e.target.value)} value={selectedTrend}>
          <option value="Daily Sales">Daily Sales</option>
          <option value="Peak Days">Peak Days</option>
          <option value="Total Orders">Total Orders</option>
          <option value="Product Usage">Product Usage</option>
        </select>
      </div>

      <div>
        <label>Choose Time Range:</label>
        <select onChange={(e) => setTimeRange(e.target.value)} value={timeRange}>
          <option value="Last 7 Days">Last 7 Days</option>
          <option value="Last 30 Days">Last 30 Days</option>
          <option value="All Time">All Time</option>
        </select>
      </div>

      <div>
        <h3>{selectedTrend} over the selected time range:</h3>
        <Line data={chartData} />
      </div>
    </div>
  );
};

export default Trends;
