import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Menu = () => {
  const [menu, setMenu] = useState([]);
  const [cart, setCart] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("All");
  const navigate = useNavigate();

  const categories = [
    "All",
    "Fruit Tea",
    "Brewed Tea",
    "Milk Tea",
    "Ice Blended",
    "Fresh Milk",
    "Signature",
    "Seasonal",
    "Additional Toppings"
  ];

  useEffect(() => {
    fetchMenu("All");
  }, []);

  const fetchMenu = async (type) => {
    try {
      let res;
      if (type === "All") {
        res = await axios.get("http://localhost:5050/api/menu");
      } else {
        const encodedType = encodeURIComponent(type); // encode for spaces
        res = await axios.get(`http://localhost:5050/api/menu/${encodedType}`);
      }
      console.log("Fetched menu for:", type, res.data);
      setMenu(res.data);
      setSelectedCategory(type);
    } catch (err) {
      console.error("Failed to load menu:", err);
      setMenu([]); // ensure menu clears if error occurs
    }
  };

  const addToCart = (item) => {
    const sugar = prompt("Sugar level? (No Sugar, 30%, 50%, 70%, 100%)", "100%");
    const ice = prompt("Ice level? (No Ice, Less Ice, Normal Ice, Hot)", "Normal Ice");
    const topping = prompt("Topping? (None, Boba, Jelly, etc.)", "Boba");

    const newItem = {
      itemid: item.itemid,
      itemname: item.itemname,
      price: item.itemprice,
      customization: `Sugar: ${sugar}, Ice: ${ice}, Topping: ${topping}`
    };

    const updatedCart = [...cart, newItem];
    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
    alert(`${item.itemname} added to cart!`);
  };

  const goToCart = () => {
    navigate("/cart");
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>ðŸ¥¤ Bubble Tea Menu</h2>

      {/* Category buttons */}
      <div style={{ marginBottom: "10px" }}>
        {categories.map((cat) => (
          <button
            key={cat}
            onClick={() => fetchMenu(cat)}
            style={{
              marginRight: "10px",
              backgroundColor: cat === selectedCategory ? "#5ab" : "#ccc",
              color: "white",
              border: "none",
              padding: "8px 12px",
              borderRadius: "6px",
              cursor: "pointer"
            }}
          >
            {cat}
          </button>
        ))}
        <button onClick={goToCart} style={{ float: "right" }}>ðŸ›’ View Cart</button>
      </div>

      {/* Menu items */}
      {menu.length === 0 ? (
        <p>No items found.</p>
      ) : (
        <ul style={{ paddingLeft: 0 }}>
          {menu.map((item) => (
            <li key={item.itemid} style={{ marginBottom: "20px", listStyle: "none" }}>
              <strong>{item.itemname}</strong> â€” ${item.itemprice.toFixed(2)}<br />
              <button onClick={() => addToCart(item)}>Add to Order</button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Menu;
