import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const SeasonalItemsList = () => {
  const [items, setItems] = useState([]);
  const [editItem, setEditItem] = useState(null);
  const navigate = useNavigate();

  // Ëé∑ÂèñÊâÄÊúâÂ≠£ËäÇÊÄßËèúÂçïÈ°π
  useEffect(() => {
    fetchItems();
  }, []);

  const fetchItems = async () => {
    try {
      const response = await axios.get("/api/menu/seasonal");
      setItems(response.data);
    } catch (err) {
      alert("‚ùå Failed to fetch items: " + err.message);
    }
  };

  // Âà†Èô§ËèúÂçïÈ°π
  const deleteItem = async (itemId) => {
    try {
      await axios.delete(`/api/menu/${itemId}`);
      alert("‚úÖ Item deleted successfully!");
      fetchItems(); // Êõ¥Êñ∞ÂàóË°®
    } catch (err) {
      alert("‚ùå Failed to delete item: " + err.message);
    }
  };

  // ÂêØÁî®Êõ¥Êñ∞ÂäüËÉΩ
  const editSeasonalItem = (item) => {
    setEditItem(item); // ËÆæÁΩÆÁºñËæëÁöÑÈ°πÁõÆ
    navigate("/edit-seasonal-item"); // Ë∑≥ËΩ¨Âà∞Êõ¥Êñ∞È°µÈù¢
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>üçΩÔ∏è Seasonal Menu Items</h2>
      <table border="1" style={{ width: "100%" }}>
        <thead>
          <tr>
            <th>Item Name</th>
            <th>Item Type</th>
            <th>Price</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {items.map((item) => (
            <tr key={item.itemid}>
              <td>{item.itemname}</td>
              <td>{item.itemtype}</td>
              <td>${item.itemprice.toFixed(2)}</td>
              <td>
                <button onClick={() => editSeasonalItem(item)}>Edit</button>
                <button onClick={() => deleteItem(item.itemid)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <button onClick={() => navigate("/add-seasonal-item")}>Add Seasonal Item</button>
    </div>
  );
};

export default SeasonalItemsList;
