import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const AddSeasonalItem = () => {
  const [formData, setFormData] = useState({
    itemname: "",
    itemtype: "Seasonal", // ÈªòËÆ§Á±ªÂûã‰∏∫"Seasonal"
    itemprice: ""
  });

  const navigate = useNavigate();

  // Â§ÑÁêÜËæìÂÖ•Ê°ÜÂÜÖÂÆπ
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  // Ê∑ªÂä†ËèúÂçïÈ°π
  const addItem = async () => {
    const { itemname, itemtype, itemprice } = formData;
    if (!itemname || !itemtype || !itemprice) {
      alert("Please fill in all fields.");
      return;
    }

    const price = parseFloat(itemprice);
    if (isNaN(price)) {
      alert("Invalid price. Please enter a number.");
      return;
    }

    try {
      await axios.post("/api/menu/seasonal", {
        itemname,
        itemtype,
        itemprice: price
      });
      alert("Seasonal menu item added successfully!");
      setFormData({ itemname: "", itemtype: "Seasonal", itemprice: "" });
    } catch (err) {
      alert("Failed to add item: " + err.message);
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>üçπ Add Seasonal Menu Item</h2>

      <input
        name="itemname"
        placeholder="Item Name"
        value={formData.itemname}
        onChange={handleChange}
      /><br />

      <input
        name="itemtype"
        placeholder="Item Type"
        value={formData.itemtype}
        onChange={handleChange}
      /><br />

      <input
        name="itemprice"
        placeholder="Price ($)"
        value={formData.itemprice}
        onChange={handleChange}
      /><br />

      <button onClick={addItem}>Add Item</button>
      <button onClick={() => navigate("/")}>Back</button>
    </div>
  );
};

export default AddSeasonalItem;
