// src/components/DrinkMenu.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom"; // ‚úÖ ÂØºÂÖ• useNavigate

const DrinkMenu = () => {
  const [drinkName, setDrinkName] = useState("");
  const [price, setPrice] = useState("");
  const [menuList, setMenuList] = useState([]);
  const navigate = useNavigate(); // ‚úÖ ÂàùÂßãÂåñ navigate

  // Ëé∑ÂèñËèúÂçïÊï∞ÊçÆ
  const viewMenu = async () => {
    try {
      const res = await axios.get("http://localhost:5050/api/menu");
      setMenuList(res.data);
    } catch (err) {
      alert("‚ùå Failed to fetch menu.");
    }
  };

  // Ê∑ªÂä†Êñ∞È•ÆÂìÅ
  const addDrink = async () => {
    if (!drinkName || !price) {
      alert("Please fill out all fields.");
      return;
    }
    try {
      await axios.post("http://localhost:5050/api/menu/add", {
        itemname: drinkName,
        itemtype: "Seasonal",  // ÈªòËÆ§ËÆæÁΩÆ‰∏∫ Seasonal
        itemprice: parseFloat(price),
      });
      alert("‚úÖ Drink added successfully!");
      setDrinkName("");
      setPrice("");
      viewMenu();
    } catch (err) {
      alert("‚ùå Failed to add drink.");
    }
  };

  // Âà†Èô§È•ÆÂìÅ
  const deleteDrink = async () => {
    if (!drinkName) {
      alert("Please enter the drink name to delete.");
      return;
    }
    try {
      await axios.post("http://localhost:5050/api/menu/delete", {
        itemname: drinkName,
      });
      alert("üóëÔ∏è Drink deleted successfully!");
      viewMenu();
    } catch (err) {
      alert("‚ùå Failed to delete drink.");
    }
  };

  // È°µÈù¢Âä†ËΩΩÊó∂Ëé∑ÂèñËèúÂçïÊï∞ÊçÆ
  useEffect(() => {
    viewMenu();
  }, []);

  // ËøîÂõû‰∏ä‰∏ÄÈ°µ
  const handleBack = () => {
    navigate(-1); // ÊàñËÄÖ navigate("/manager") Ë∑≥Âõû Manager È°µÈù¢
  };

  return (
    <div style={{ padding: "20px" }}>
      <button onClick={handleBack} style={{ marginBottom: "10px" }}>
        üîô Back
      </button>

      <h2>ü•§ Drink Menu Management</h2>

      <div style={{ marginBottom: "20px" }}>
        <input
          type="text"
          placeholder="Drink Name"
          value={drinkName}
          onChange={(e) => setDrinkName(e.target.value)}
        />
        <input
          type="number"
          placeholder="Price"
          value={price}
          onChange={(e) => setPrice(e.target.value)}
        />
        <div style={{ marginTop: "10px" }}>
          <button onClick={addDrink}>‚ûï Add Drink</button>
          <button onClick={deleteDrink}>üóëÔ∏è Delete Drink</button>
          <button onClick={viewMenu}>üîÑ Refresh Menu</button>
        </div>
      </div>

      <h3>üìã Current Seasonal Menu</h3>
      {menuList.length === 0 ? (
        <p>No seasonal items available.</p>
      ) : (
        <table border="1" cellPadding="8" style={{ borderCollapse: "collapse", width: "100%" }}>
          <thead style={{ backgroundColor: "#f2f2f2" }}>
            <tr>
              <th>ID</th>
              <th>Drink Name</th>
              <th>Category</th>
              <th>Price ($)</th>
              <th>Available</th>
            </tr>
          </thead>
          <tbody>
            {menuList.filter(item => item.itemtype === "Seasonal").map((item) => (
              <tr key={item.itemid}>
                <td>{item.itemid}</td>
                <td>{item.itemname}</td>
                <td>{item.itemtype}</td>
                <td>{parseFloat(item.itemprice).toFixed(2)}</td>
                <td>{item.available ? "Yes" : "No"}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default DrinkMenu;
