import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Inventory = () => {
  const [ingredients, setIngredients] = useState([]);
  const [newIngredient, setNewIngredient] = useState({ name: "", quantity: "" });
  const navigate = useNavigate();

  useEffect(() => {
    fetchIngredients();
  }, []);

  const fetchIngredients = async () => {
    try {
      const res = await axios.get("http://localhost:5050/api/inventory");
      setIngredients(res.data);
    } catch (err) {
      console.error("Error fetching inventory:", err);
    }
  };

  const handleAdd = async () => {
    if (!newIngredient.name || !newIngredient.quantity) {
      alert("Please enter both name and quantity.");
      return;
    }
    try {
      await axios.post("http://localhost:5050/api/inventory/add", {
        ingredientname: newIngredient.name,
        quantity: parseInt(newIngredient.quantity)
      });
      alert("‚úÖ Ingredient added.");
      setNewIngredient({ name: "", quantity: "" });
      fetchIngredients();
    } catch (err) {
      console.error("Error adding ingredient:", err);
      alert("‚ùå Failed to add ingredient.");
    }
  };

  const handleDelete = async (name) => {
    if (!window.confirm(`Are you sure you want to delete "${name}"?`)) return;

    try {
      await axios.delete("http://localhost:5050/api/inventory/delete", {
        data: { ingredientname: name }
      });
      alert("üóëÔ∏è Ingredient deleted.");
      fetchIngredients();
    } catch (err) {
      console.error("Error deleting ingredient:", err);
      alert("‚ùå Failed to delete ingredient.");
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <button onClick={() => navigate(-1)} style={{ marginBottom: "10px" }}>
        üîô Back
      </button>

      <h2>üì¶ Inventory Management</h2>

      <div style={{ marginBottom: "20px" }}>
        <input
          type="text"
          placeholder="Ingredient Name"
          value={newIngredient.name}
          onChange={(e) => setNewIngredient({ ...newIngredient, name: e.target.value })}
        />
        <input
          type="number"
          placeholder="Quantity"
          value={newIngredient.quantity}
          onChange={(e) => setNewIngredient({ ...newIngredient, quantity: e.target.value })}
        />
        <button onClick={handleAdd}>‚ûï Add Ingredient</button>
      </div>

      <ul style={{ listStyle: "none", padding: 0 }}>
        {ingredients.map((item, idx) => (
          <li key={idx} style={{ marginTop: "10px" }}>
            <strong>{item.ingredientname}</strong> ‚Äî {item.quantity}
            <button onClick={() => handleDelete(item.ingredientname)} style={{ marginLeft: "10px" }}>
              ‚ùå Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Inventory;
