import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Cart = () => {
  const [cart, setCart] = useState([]);
  const [customerName, setCustomerName] = useState("");
  const [userID, setUserID] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const stored = JSON.parse(localStorage.getItem("cart")) || [];
    setCart(stored);
  }, []);

  const removeItem = (index) => {
    const newCart = [...cart];
    newCart.splice(index, 1);
    setCart(newCart);
    localStorage.setItem("cart", JSON.stringify(newCart));
  };

  const handleSubmit = async () => {
    if (!customerName || !userID) {
      alert("Please enter Customer Name and User ID.");
      return;
    }

    try {
      const res = await axios.post("http://localhost:5050/api/order", {
        customerName,
        userID,
        items: cart
      });

      if (res.data.success) {
        alert(`Order placed! Order ID: ${res.data.orderid}`);
        localStorage.removeItem("cart");
        setCart([]);
        navigate("/menu");
      }
    } catch (err) {
      console.error("Submit error:", err);
      alert("Failed to submit order.");
    }
  };

  const total = cart.reduce((sum, item) => sum + item.price, 0);

  return (
    <div style={{ padding: 20 }}>
      <h2>üßæ Your Cart</h2>

      <input
        type="text"
        placeholder="Customer Name"
        value={customerName}
        onChange={(e) => setCustomerName(e.target.value)}
      />
      <input
        type="number"
        placeholder="User ID"
        value={userID}
        onChange={(e) => setUserID(e.target.value)}
      />

      <ul>
        {cart.map((item, index) => (
          <li key={index}>
            <strong>{item.itemname}</strong> ‚Äî ${item.price.toFixed(2)}<br />
            {item.customization}
            <button onClick={() => removeItem(index)}>‚ùå Remove</button>
          </li>
        ))}
      </ul>

      <h3>Total: ${total.toFixed(2)}</h3>
      <button onClick={handleSubmit}>‚úÖ Submit Order</button>
    </div>
  );
};

export default Cart;
