import React, { useEffect, useState } from "react";
import axios from "axios";
import { Line } from "react-chartjs-2";
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from "chart.js";
import { useNavigate } from "react-router-dom";

// Ê≥®ÂÜåChart.jsÁªÑ‰ª∂
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

const ProductUsage = () => {
  const [salesData, setSalesData] = useState([]);
  const [dateLabels, setDateLabels] = useState([]);
  const [timeRange, setTimeRange] = useState("Last 7 Days");
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");
  const navigate = useNavigate(); // Áî®‰∫éÂØºËà™

  // Ëé∑Âèñ‰∫ßÂìÅ‰ΩøÁî®Êï∞ÊçÆ
  useEffect(() => {
    loadTrendData(timeRange);
  }, [timeRange]);

  const loadTrendData = async (range) => {
    const start = getStartDate(range);
    const end = getEndDate(range);

    if (!isValidDate(start) || !isValidDate(end)) {
      alert("Invalid date format. Please use YYYY-MM-DD.");
      return;
    }

    try {
      const response = await axios.get("http://localhost:5050/api/productusage", {
        params: {
          startDate: start,
          endDate: end,
        },
      });

      const trendData = response.data;
      setSalesData(trendData.map((item) => item.totalUsed));
      setDateLabels(trendData.map((item) => item.itemName));
    } catch (err) {
      console.error("Error fetching trend data:", err);
      alert("‚ùå Failed to fetch trend data.");
    }
  };

  const handleTimeRangeChange = (e) => {
    setTimeRange(e.target.value);
  };

  // Ëé∑ÂèñÂºÄÂßãÊó•Êúü
  const getStartDate = (range) => {
    const today = new Date();
    let startDate = "";

    if (range === "Last 7 Days") {
      const last7Days = new Date(today);
      last7Days.setDate(today.getDate() - 7);
      startDate = last7Days.toISOString().split("T")[0];
    } else if (range === "Last 30 Days") {
      const last30Days = new Date(today);
      last30Days.setDate(today.getDate() - 30);
      startDate = last30Days.toISOString().split("T")[0];
    } else if (range === "Custom Range") {
      startDate = startDate;
    } else {
      startDate = today.toISOString().split("T")[0]; // Default to today
    }

    return startDate;
  };

  // Ëé∑ÂèñÁªìÊùüÊó•Êúü
  const getEndDate = (range) => {
    const today = new Date();
    let endDate = "";

    if (range === "Custom Range") {
      endDate = endDate;
    } else {
      endDate = today.toISOString().split("T")[0]; // Default to today
    }

    return endDate;
  };

  // Ê£ÄÊü•Êó•ÊúüÊ†ºÂºè
  const isValidDate = (date) => {
    const regex = /^\d{4}-\d{2}-\d{2}$/;
    return regex.test(date);
  };

  // ÈÖçÁΩÆÂõæË°®ÁöÑÊï∞ÊçÆÂíåÈÄâÈ°π
  const chartData = {
    labels: dateLabels, // xËΩ¥ÔºöÊó•Êúü
    datasets: [
      {
        label: "Product Usage",
        data: salesData, // yËΩ¥Ôºö‰ΩøÁî®Êï∞ÊçÆ
        fill: false,
        borderColor: "rgba(75, 192, 192, 1)", // ËÆæÁΩÆÊäòÁ∫øÈ¢úËâ≤
        tension: 0.1,
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    scales: {
      x: {
        title: {
          display: true,
          text: "Product Name",
        },
      },
      y: {
        title: {
          display: true,
          text: "Total Used",
        },
      },
    },
  };

  return (
    <div style={{ padding: "20px" }}>
      <button onClick={() => navigate(-1)} style={{ marginBottom: "10px" }}>
        üîô Back
      </button>

      <h2>üìä Product Usage Report</h2>

      <div>
        <h3>Select Time Range</h3>
        <select value={timeRange} onChange={handleTimeRangeChange}>
          <option value="Last 7 Days">Last 7 Days</option>
          <option value="Last 30 Days">Last 30 Days</option>
          <option value="Custom Range">Custom Range</option>
        </select>
      </div>

      {timeRange === "Custom Range" && (
        <div>
          <input
            type="date"
            value={startDate}
            onChange={(e) => setStartDate(e.target.value)}
            placeholder="Start Date"
          />
          <input
            type="date"
            value={endDate}
            onChange={(e) => setEndDate(e.target.value)}
            placeholder="End Date"
          />
        </div>
      )}

      <button onClick={() => loadTrendData(timeRange)}>Apply Filter</button>

      <div>
        <h3>Product Usage Chart</h3>
        <Line data={chartData} options={chartOptions} />
      </div>

      <div>
        <h3>Product Usage Data</h3>
        <table border="1" style={{ width: "100%" }}>
          <thead>
            <tr>
              <th>Product</th>
              <th>Total Used</th>
            </tr>
          </thead>
          <tbody>
            {dateLabels.map((label, index) => (
              <tr key={index}>
                <td>{label}</td>
                <td>{salesData[index]}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ProductUsage;
