// components/CashPaymentPage.js
import React, { useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";

const CashPaymentPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const orderTotal = location.state?.total || 0;

  const [input, setInput] = useState("");
  const [change, setChange] = useState(null);

  const handleKey = (val) => {
    if (val === "C") return setInput("");
    if (val === "âŒ«") return setInput((prev) => prev.slice(0, -1));
    setInput((prev) => prev + val);
  };

  const handleSubmit = () => {
    const received = parseFloat(input);
    if (isNaN(received)) {
      alert("Invalid amount.");
      return;
    }
    if (received < orderTotal) {
      alert("Insufficient amount.");
      return;
    }
    setChange(received - orderTotal);
    setTimeout(() => {
      navigate("/thankyou", { state: { total: orderTotal } });
    }, 1500);
  };

  const keypad = ["1", "2", "3", "4", "5", "6", "7", "8", "9", "C", "0", "âŒ«"];

  return (
    <div style={{ padding: "30px", textAlign: "center" }}>
      <h2>ðŸ’µ Cash Payment</h2>
      <p>Total Due: ${orderTotal.toFixed(2)}</p>
      <input value={input} readOnly style={{ fontSize: "20px", textAlign: "center" }} />
      <div style={{ display: "grid", gridTemplateColumns: "repeat(3, 1fr)", gap: "10px", marginTop: "20px" }}>
        {keypad.map((key) => (
          <button key={key} onClick={() => handleKey(key)}>{key}</button>
        ))}
      </div>
      <button onClick={handleSubmit} style={{ marginTop: "20px" }}>
        âœ… Submit Payment
      </button>
      {change !== null && <p style={{ marginTop: "10px" }}>Change: ${change.toFixed(2)}</p>}
    </div>
  );
};

export default CashPaymentPage;
