import React, { useState, useEffect } from "react";
import axios from "axios";
import { Line } from 'react-chartjs-2';
import { useNavigate } from "react-router-dom";
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from 'chart.js';

// 注册Chart.js组件
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

const Trends = () => {
  const [salesData, setSalesData] = useState([]);
  const [dateLabels, setDateLabels] = useState([]);
  const [currentTrend, setCurrentTrend] = useState("Daily Sales");
  const [timeRange, setTimeRange] = useState("Last 7 Days");
  const navigate = useNavigate();

  // 获取趋势数据
  useEffect(() => {
    loadTrendData(currentTrend);
  }, [currentTrend, timeRange]);

  const loadTrendData = async (trendType) => {
    try {
      const response = await axios.get("http://localhost:5050/api/trends", {
        params: {
          trendType: trendType,
          timeRange: timeRange,
        },
      });
      const trendData = response.data;
      setSalesData(trendData.map((item) => item.total_amount)); // 取出销售金额
      setDateLabels(trendData.map((item) => item.date)); // 取出日期
    } catch (err) {
      console.error("Error fetching trend data:", err);
      alert("❌ Failed to fetch trend data.");
    }
  };

  const handleTrendChange = (trendType) => {
    setCurrentTrend(trendType);
  };

  const handleTimeRangeChange = (event) => {
    setTimeRange(event.target.value);
  };

  // 配置图表的数据和选项
  const chartData = {
    labels: dateLabels, // x轴：日期
    datasets: [
      {
        label: currentTrend,
        data: salesData, // y轴：趋势数据
        fill: false,
        borderColor: 'rgba(75, 192, 192, 1)', // 设置折线颜色
        tension: 0.1,
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    scales: {
      x: {
        title: {
          display: true,
          text: 'Date',
        },
      },
      y: {
        title: {
          display: true,
          text: 'Value',
        },
      },
    },
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>📊 Trends Report</h2>

      <div>
        <h3>Select Trend Type</h3>
        <button onClick={() => handleTrendChange("Daily Sales")}>Daily Sales</button>
        <button onClick={() => handleTrendChange("Peak Days")}>Peak Days</button>
        <button onClick={() => handleTrendChange("Total Orders")}>Total Orders</button>
        <button onClick={() => handleTrendChange("Product Usage")}>Product Usage</button>
      </div>

      <div>
        <h3>Select Time Range</h3>
        <select value={timeRange} onChange={handleTimeRangeChange}>
          <option value="Last 7 Days">Last 7 Days</option>
          <option value="Last 30 Days">Last 30 Days</option>
          <option value="All Time">All Time</option>
        </select>
      </div>

      <div>
        <h3>Data Chart</h3>
        <Line data={chartData} options={chartOptions} />
      </div>

      <div>
        <h3>Trend Data</h3>
        <table border="1" style={{ width: "100%" }}>
          <thead>
            <tr>
              <th>Date</th>
              <th>Value</th>
            </tr>
          </thead>
          <tbody>
            {dateLabels.map((date, index) => (
              <tr key={index}>
                <td>{date}</td>
                <td>{salesData[index]}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <button style={{ marginTop: "20px" }} onClick={() => navigate("/")}>
        ⬅️ Back to Manager
      </button>
    </div>
  );
};

export default Trends;
