import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Cart = () => {
  const [cart, setCart] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const storedCart = JSON.parse(localStorage.getItem("cart")) || [];
    setCart(storedCart);
  }, []);

  const removeItem = (index) => {
    const updated = [...cart];
    updated.splice(index, 1);
    setCart(updated);
    localStorage.setItem("cart", JSON.stringify(updated));
  };

  const submitOrder = async () => {
    const customerName = prompt("Enter your name:");
    const userId = prompt("Enter your user ID:");

    if (!customerName || !userId) {
      alert("Customer name and user ID are required.");
      return;
    }

    try {
      const response = await axios.post("http://localhost:5050/api/orders", {
        customerName,
        userId,
        items: cart.map((item) => ({
          itemid: item.itemid,
          price: item.price,
        })),
        paymentMethod: "Cash",
      });

      alert("Order submitted successfully!");
      setCart([]);
      localStorage.removeItem("cart");
      navigate("/menu"); // Optional: Go back to menu
    } catch (err) {
      console.error(err);
      alert("Failed to submit order.");
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>ðŸ›’ Cart</h2>
      {cart.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <>
          <ul>
            {cart.map((item, index) => (
              <li key={index} style={{ marginBottom: "15px" }}>
                <strong>{item.itemname}</strong> â€” ${parseFloat(item.price).toFixed(2)}<br />
                <small>{item.customization}</small><br />
                <button onClick={() => removeItem(index)}>Remove</button>
              </li>
            ))}
          </ul>
          <button onClick={submitOrder}>Submit Order</button>
        </>
      )}
    </div>
  );
};

export default Cart;
