import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Menu = () => {
  const [menu, setMenu] = useState([]);
  const [cart, setCart] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // Ëé∑ÂèñËèúÂçïÊï∞ÊçÆ
    const fetchMenu = async () => {
      try {
        const res = await axios.get("http://localhost:5050/api/menu");
        setMenu(res.data);
      } catch (err) {
        console.error("Menu fetch failed:", err);
      }
    };

    fetchMenu();
  }, []);

  // Ê∑ªÂä†Âà∞Ë¥≠Áâ©ËΩ¶
  const addToCart = (item) => {
    setCart((prevCart) => [...prevCart, item]);
  };

  // Êü•ÁúãË¥≠Áâ©ËΩ¶
  const goToCart = () => {
    // Â∞ÜË¥≠Áâ©ËΩ¶ÂÜÖÂÆπ‰º†ÈÄíÁªô‰∏ã‰∏Ä‰∏™È°µÈù¢ÔºàËøôÈáåÁÆÄÂçïÊñπÂºèÁî® localStorageÔºâ
    localStorage.setItem("cart", JSON.stringify(cart));
    navigate("/cart");
  };

  return (
    <div>
      <h2>Menu</h2>
      <ul>
        {menu.map((item) => (
          <li key={item.itemid} style={{ marginBottom: "20px" }}>
            <strong>{item.itemname}</strong> ‚Äî ${item.price.toFixed(2)} <br />
            <small>Ingredients: {item.ingredients.join(", ")}</small> <br />
            <button onClick={() => addToCart(item)}>Add to Cart</button>
          </li>
        ))}
      </ul>

      {cart.length > 0 && (
        <div style={{ marginTop: "30px" }}>
          <button onClick={goToCart}>üõí View Cart ({cart.length})</button>
        </div>
      )}
    </div>
  );
};

export default Menu;
