import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Trends = () => {
  const [salesData, setSalesData] = useState([]);
  const [dateLabels, setDateLabels] = useState([]);
  const [currentTrend, setCurrentTrend] = useState("Daily Sales");
  const [timeRange, setTimeRange] = useState("Last 7 Days");
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    loadTrendData(currentTrend);
  }, [currentTrend, timeRange]);

  const loadTrendData = async (trendType) => {
    setIsLoading(true);
    setSalesData([]);
    setDateLabels([]);

    const timeFilter = getTimeFilter();
    const queryParams = {
      trendType,
      timeFilter,
    };

    try {
      const response = await axios.get("http://localhost:5050/api/trends", {
        params: queryParams,
      });

      setSalesData(response.data.salesData);
      setDateLabels(response.data.dateLabels);
    } catch (err) {
      console.error("Error fetching trend data:", err);
    } finally {
      setIsLoading(false);
    }
  };

  const getTimeFilter = () => {
    switch (timeRange) {
      case "Last 7 Days":
        return " WHERE date >= CURRENT_DATE - INTERVAL '7 days'";
      case "Last 30 Days":
        return " WHERE date >= CURRENT_DATE - INTERVAL '30 days'";
      case "All Time":
      default:
        return "";
    }
  };

  const handleTrendChange = (trend) => {
    setCurrentTrend(trend);
  };

  const handleTimeRangeChange = (e) => {
    setTimeRange(e.target.value);
  };

  const goBack = () => {
    navigate("/manager");
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>📊 Trends Report</h2>

      <div>
        <button onClick={() => handleTrendChange("Daily Sales")}>Daily Sales</button>
        <button onClick={() => handleTrendChange("Peak Days")}>Peak Days</button>
        <button onClick={() => handleTrendChange("Total Orders")}>Total Orders</button>
        <button onClick={() => handleTrendChange("Product Usage")}>Product Usage</button>
      </div>

      <div>
        <select value={timeRange} onChange={handleTimeRangeChange}>
          <option value="Last 7 Days">Last 7 Days</option>
          <option value="Last 30 Days">Last 30 Days</option>
          <option value="All Time">All Time</option>
        </select>
      </div>

      {isLoading ? (
        <p>Loading...</p>
      ) : (
        <div>
          <h3>{currentTrend} Report</h3>
          <div style={{ display: 'flex' }}>
            <div style={{ marginRight: '20px', width: '300px' }}>
              <h4>Report Details:</h4>
              <pre>
                {dateLabels.map((date, index) => (
                  <div key={index}>
                    {date}: {salesData[index]}
                  </div>
                ))}
              </pre>
            </div>
            <div>
              <h4>Chart:</h4>
              <div style={{ width: "500px", height: "300px", border: '1px solid #000' }}>
                {/* Chart drawing area */}
                <CanvasChart salesData={salesData} dateLabels={dateLabels} />
              </div>
            </div>
          </div>
        </div>
      )}

      <button onClick={goBack} style={{ marginTop: "20px" }}>
        ⬅️ Back to Manager Page
      </button>
    </div>
  );
};

const CanvasChart = ({ salesData, dateLabels }) => {
  const canvasRef = React.useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    drawChart(ctx);
  }, [salesData]);

  const drawChart = (ctx) => {
    const padding = 50;
    const width = 500 - 2 * padding;
    const height = 300 - 2 * padding;
    if (salesData.length === 0) return;

    const maxSales = Math.max(...salesData);
    const numPoints = salesData.length;
    const xStep = width / Math.max(numPoints - 1, 1);

    ctx.clearRect(0, 0, 500, 300); // clear canvas

    ctx.beginPath();
    ctx.moveTo(padding, height + padding - (salesData[0] / maxSales) * height);
    for (let i = 1; i < numPoints; i++) {
      const x = padding + i * xStep;
      const y = height + padding - (salesData[i] / maxSales) * height;
      ctx.lineTo(x, y);
    }

    ctx.strokeStyle = 'blue';
    ctx.stroke();
  };

  return <canvas ref={canvasRef} width={500} height={300}></canvas>;
};

export default Trends;
