// src/components/Cashier.js
import React from "react";
import { useNavigate } from "react-router-dom";

const CashierDashboard = () => {
  const navigate = useNavigate();

  const handleNavigation = (category) => {
    navigate(`/menu-category/${category}`);
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center p-6">
      <img
        src="/logo192.png"
        alt="Logo"
        className="w-28 mb-4"
      />
      <h1 className="text-3xl font-bold text-blue-600 mb-6">ðŸ’¼ Cashier Dashboard</h1>

      <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-4 w-full max-w-4xl">
        <button className="bg-blue-500 text-white py-3 px-4 rounded-lg hover:bg-blue-600" onClick={() => handleNavigation("Fruit Tea")}>Fruit Tea</button>
        <button className="bg-blue-500 text-white py-3 px-4 rounded-lg hover:bg-blue-600" onClick={() => handleNavigation("Milk Tea")}>Milk Tea</button>
        <button className="bg-blue-500 text-white py-3 px-4 rounded-lg hover:bg-blue-600" onClick={() => handleNavigation("Brewed Tea")}>Brewed Tea</button>
        <button className="bg-blue-500 text-white py-3 px-4 rounded-lg hover:bg-blue-600" onClick={() => handleNavigation("Ice Blended")}>Ice Blended</button>
        <button className="bg-blue-500 text-white py-3 px-4 rounded-lg hover:bg-blue-600" onClick={() => handleNavigation("Fresh Milk")}>Fresh Milk</button>
        <button className="bg-blue-500 text-white py-3 px-4 rounded-lg hover:bg-blue-600" onClick={() => handleNavigation("Signature")}>Signature</button>
        <button className="bg-blue-500 text-white py-3 px-4 rounded-lg hover:bg-blue-600" onClick={() => handleNavigation("Seasonal")}>Seasonal</button>
        <button className="bg-blue-500 text-white py-3 px-4 rounded-lg hover:bg-blue-600" onClick={() => handleNavigation("Additional Toppings")}>Additional Toppings</button>

        {/* âœ… New Order History button */}
        <button className="bg-green-500 text-white py-3 px-4 rounded-lg hover:bg-green-600" onClick={() => navigate("/order-history")}>
          ðŸ§¾ Order History
        </button>

        {/* Logout */}
        <button className="bg-red-500 text-white py-3 px-4 rounded-lg hover:bg-red-600" onClick={() => navigate("/")}>
          ðŸšª Logout
        </button>
      </div>
    </div>
  );
};

export default CashierDashboard;
