import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const AddSeasonalItem = () => {
  const [formData, setFormData] = useState({
    itemname: "",
    itemprice: "",
  });
  const [seasonalMenu, setSeasonalMenu] = useState([]);
  const navigate = useNavigate();

  // Ëé∑ÂèñÁé∞ÊúâÁöÑSeasonalËèúÂçïÈ°π
  const fetchSeasonalMenu = async () => {
    try {
      const res = await axios.get("http://localhost:5050/api/menu/seasonal");
      setSeasonalMenu(res.data);
    } catch (err) {
      alert("‚ùå Failed to fetch seasonal menu.");
    }
  };

  // Êõ¥Êñ∞Ë°®ÂçïËæìÂÖ•
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  // Ê∑ªÂä†SeasonalËèúÂçïÈ°π
  const addSeasonalItem = async () => {
    const { itemname, itemprice } = formData;
    if (!itemname || !itemprice) {
      alert("Please fill in all fields.");
      return;
    }

    const price = parseFloat(itemprice);
    if (isNaN(price)) {
      alert("Invalid price. Please enter a valid number.");
      return;
    }

    try {
      await axios.post("http://localhost:5050/api/menu/add", {
        itemname,
        itemtype: "Seasonal", // Âº∫Âà∂ËÆæÁΩÆ‰∏∫Seasonal
        itemprice: price,
      });
      alert("‚úÖ Seasonal item added successfully!");
      setFormData({ itemname: "", itemprice: "" });
      fetchSeasonalMenu(); // Âà∑Êñ∞Áé∞ÊúâËèúÂçïÈ°π
    } catch (err) {
      alert("‚ùå Failed to add seasonal item.");
    }
  };

  // Âà†Èô§SeasonalËèúÂçïÈ°π
  const deleteSeasonalItem = async (itemid) => {
    try {
      await axios.post("http://localhost:5050/api/menu/delete", {
        itemid,
      });
      alert("üóëÔ∏è Seasonal item deleted successfully!");
      fetchSeasonalMenu(); // Âà∑Êñ∞Áé∞ÊúâËèúÂçïÈ°π
    } catch (err) {
      alert("‚ùå Failed to delete seasonal item.");
    }
  };

  // ÁªÑ‰ª∂Âä†ËΩΩÊó∂Ëé∑ÂèñÁé∞ÊúâËèúÂçï
  useEffect(() => {
    fetchSeasonalMenu();
  }, []);

  return (
    <div style={{ padding: "20px" }}>
      <h2>üçπ Add Seasonal Menu Item</h2>

      <div>
        <input
          name="itemname"
          placeholder="Item Name"
          value={formData.itemname}
          onChange={handleChange}
        /><br />

        <input
          name="itemprice"
          placeholder="Price ($)"
          value={formData.itemprice}
          onChange={handleChange}
        /><br />

        <button onClick={addSeasonalItem}>‚ûï Add Seasonal Item</button>
      </div>

      <h3>Current Seasonal Menu</h3>
      {seasonalMenu.length === 0 ? (
        <p>No seasonal items available.</p>
      ) : (
        <table border="1" style={{ width: "100%" }}>
          <thead>
            <tr>
              <th>ID</th>
              <th>Item Name</th>
              <th>Price ($)</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {seasonalMenu.map((item) => (
              <tr key={item.itemid}>
                <td>{item.itemid}</td>
                <td>{item.itemname}</td>
                <td>{item.itemprice}</td>
                <td>
                  <button onClick={() => deleteSeasonalItem(item.itemid)}>
                    üóëÔ∏è Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      <button style={{ marginTop: "20px" }} onClick={() => navigate("/")}>
        ‚¨ÖÔ∏è Back to Manager
      </button>
    </div>
  );
};

export default AddSeasonalItem;
