import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Menu = () => {
  const [menu, setMenu] = useState([]);
  const [cart, setCart] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("All");
  const [customizations, setCustomizations] = useState({});
  const navigate = useNavigate();

  const categories = [
    "All",
    "Fruit Tea",
    "Milk Tea",
    "Brewed Tea",
    "Ice Blended",
    "Fresh Milk",
    "Signature",
    "Seasonal",
    "Additional Toppings",
  ];

  const sugarOptions = ["No Sugar", "30%", "50%", "70%", "100%"];
  const iceOptions = ["No Ice", "Less Ice", "Normal Ice", "Hot"];
  const toppingOptions = ["None", "Boba", "Lychee Jelly", "Pearls", "Grass Jelly", "Pudding", "Aloe Vera", "Red Beans"];

  useEffect(() => {
    fetchMenu("All");
    const storedCart = JSON.parse(localStorage.getItem("cart")) || [];
    setCart(storedCart);
  }, []);

  const fetchMenu = async (type) => {
    try {
      let res;
      if (type === "All") {
        res = await axios.get("http://localhost:5050/api/menu");
      } else {
        res = await axios.get(`http://localhost:5050/api/menu/${encodeURIComponent(type)}`);
      }
      setMenu(res.data);
      setSelectedCategory(type);
    } catch (err) {
      console.error("Failed to load menu:", err);
    }
  };

  const handleCustomizationChange = (itemid, field, value) => {
    setCustomizations((prev) => ({
      ...prev,
      [itemid]: {
        ...prev[itemid],
        [field]: value,
      },
    }));
  };

  const addToCart = (item) => {
    const { sugar = "100%", ice = "Normal Ice", topping = "None" } = customizations[item.itemid] || {};

    const newItem = {
      itemid: item.itemid,
      itemname: item.itemname,
      price: item.itemprice,
      customization: `Sugar: ${sugar} | Ice: ${ice} | Topping: ${topping}`,
    };

    const updatedCart = [...cart, newItem];
    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
    alert(`${item.itemname} added to cart!`);
  };

  const goToCart = () => {
    navigate("/cart");
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>🥤 Bubble Tea Menu</h2>

      {/* Category buttons */}
      <div style={{ marginBottom: "10px" }}>
        {categories.map((cat) => (
          <button
            key={cat}
            onClick={() => fetchMenu(cat)}
            style={{
              marginRight: "10px",
              backgroundColor: cat === selectedCategory ? "#5ab" : "#ccc",
              color: "white",
              border: "none",
              padding: "8px 12px",
              borderRadius: "6px",
              cursor: "pointer",
            }}
          >
            {cat}
          </button>
        ))}
        <button onClick={goToCart} style={{ float: "right" }}>
          🛒 View Cart
        </button>
      </div>

      {/* Menu items */}
      <ul>
        {menu.length === 0 ? (
          <p>No items found.</p>
        ) : (
          menu.map((item) => (
            <li key={item.itemid} style={{ marginBottom: "25px", listStyle: "none" }}>
              <strong>{item.itemname}</strong> — ${Number(item.itemprice).toFixed(2)}
              <br />

              {/* Customization Dropdowns */}
              <label>
                Sugar:
                <select
                  value={customizations[item.itemid]?.sugar || "100%"}
                  onChange={(e) => handleCustomizationChange(item.itemid, "sugar", e.target.value)}
                  style={{ marginLeft: "5px" }}
                >
                  {sugarOptions.map((option) => (
                    <option key={option}>{option}</option>
                  ))}
                </select>
              </label>

              <br />
              <label>
                Ice:
                <select
                  value={customizations[item.itemid]?.ice || "Normal Ice"}
                  onChange={(e) => handleCustomizationChange(item.itemid, "ice", e.target.value)}
                  style={{ marginLeft: "5px" }}
                >
                  {iceOptions.map((option) => (
                    <option key={option}>{option}</option>
                  ))}
                </select>
              </label>

              <br />
              <label>
                Topping:
                <select
                  value={customizations[item.itemid]?.topping || "None"}
                  onChange={(e) => handleCustomizationChange(item.itemid, "topping", e.target.value)}
                  style={{ marginLeft: "5px" }}
                >
                  {toppingOptions.map((option) => (
                    <option key={option}>{option}</option>
                  ))}
                </select>
              </label>

              <br />
              <button onClick={() => addToCart(item)} style={{ marginTop: "8px" }}>
                ➕ Add to Order
              </button>
            </li>
          ))
        )}
      </ul>
    </div>
  );
};

export default Menu;
