import React, { useEffect, useState } from "react";
import axios from "axios";
import { Bar } from "react-chartjs-2";
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from "chart.js";
import { useNavigate } from "react-router-dom";

// 注册 Chart.js 组件
ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const ProductUsage = () => {
  const [salesData, setSalesData] = useState([]);
  const [dateLabels, setDateLabels] = useState([]);
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");
  const [timeRange, setTimeRange] = useState("Last 7 Days"); // 默认时间范围为7天
  const navigate = useNavigate();

  useEffect(() => {
    loadTrendData();
  }, [timeRange, startDate, endDate]);

  const loadTrendData = async () => {
    try {
      const params = { startDate, endDate, timeRange };
      const response = await axios.get("http://localhost:5050/api/productusage", {
        params,
      });
      const trendData = response.data;
      setSalesData(trendData.map((item) => item.totalUsed));
      setDateLabels(trendData.map((item) => item.itemName));
    } catch (err) {
      console.error("Error fetching product usage data:", err);
      alert("❌ Failed to fetch trend data.");
    }
  };

  const chartData = {
    labels: dateLabels,
    datasets: [
      {
        label: "Total Usage",
        data: salesData,
        backgroundColor: "rgba(75, 192, 192, 0.2)",
        borderColor: "rgba(75, 192, 192, 1)",
        borderWidth: 1,
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    scales: {
      x: {
        title: {
          display: true,
          text: "Product Name",
        },
      },
      y: {
        title: {
          display: true,
          text: "Total Usage",
        },
      },
    },
  };

  const handleBack = () => {
    navigate(-1);
  };

  const handleTimeRangeChange = (event) => {
    setTimeRange(event.target.value);
    setStartDate(""); // 清空输入框以确保选择了正确的时间范围
    setEndDate("");
  };

  const handleCustomDateRange = () => {
    if (startDate && endDate) {
      loadTrendData(); // 使用自定义日期范围加载数据
    } else {
      alert("Please select a valid custom date range.");
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <button onClick={handleBack} style={{ marginBottom: "10px" }}>
        🔙 Back
      </button>

      <h2>📊 Product Usage Report</h2>

      <div>
        <label>
          Select Time Range:
          <select value={timeRange} onChange={handleTimeRangeChange}>
            <option value="Last 7 Days">Last 7 Days</option>
            <option value="Last 30 Days">Last 30 Days</option>
            <option value="Custom Range">Custom Range</option>
          </select>
        </label>
      </div>

      {timeRange === "Custom Range" && (
        <div>
          <label>
            Start Date:
            <input
              type="date"
              value={startDate}
              onChange={(e) => setStartDate(e.target.value)}
            />
          </label>
          <label>
            End Date:
            <input
              type="date"
              value={endDate}
              onChange={(e) => setEndDate(e.target.value)}
            />
          </label>
          <button onClick={handleCustomDateRange}>Apply Custom Date Range</button>
        </div>
      )}

      <div>
        <h3>Bar Chart of Product Usage</h3>
        <Bar data={chartData} options={chartOptions} />
      </div>
    </div>
  );
};

export default ProductUsage;
