import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const SeasonalMenu = () => {
  const [menuList, setMenuList] = useState([]);
  const navigate = useNavigate();

  // Ëé∑ÂèñÊâÄÊúâSeasonalËèúÂçïÈ°π
  useEffect(() => {
    viewSeasonalMenu();
  }, []);

  const viewSeasonalMenu = async () => {
    try {
      const res = await axios.get("http://localhost:5050/api/menu/seasonal");
      setMenuList(res.data); // ËÆæÁΩÆËøîÂõûÁöÑÊï∞ÊçÆ
    } catch (err) {
      alert("‚ùå Failed to fetch seasonal menu.");
    }
  };

  const handleBack = () => {
    navigate(-1); // ËøîÂõûÂà∞Ââç‰∏Ä‰∏™È°µÈù¢
  };

  return (
    <div style={{ padding: "20px" }}>
      <button onClick={handleBack} style={{ marginBottom: "10px" }}>
        üîô Back
      </button>

      <h2>üçπ Seasonal Menu Items</h2>

      {menuList.length === 0 ? (
        <p>No seasonal items available.</p>
      ) : (
        <table border="1" style={{ width: "100%", borderCollapse: "collapse" }}>
          <thead style={{ backgroundColor: "#f2f2f2" }}>
            <tr>
              <th>ID</th>
              <th>Item Name</th>
              <th>Category</th>
              <th>Price ($)</th>
              <th>Available</th>
            </tr>
          </thead>
          <tbody>
            {menuList.map((item) => (
              <tr key={item.itemid}>
                <td>{item.itemid}</td>
                <td>{item.itemname}</td>
                <td>{item.itemtype}</td>
                <td>{parseFloat(item.itemprice).toFixed(2)}</td>
                <td>{item.available ? "Yes" : "No"}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default SeasonalMenu;
