// src/components/OrdersPage.js
import React, { useEffect, useState } from "react";
import axios from "axios";

const OrdersPage = () => {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5050/api/orders")
      .then((res) => setOrders(res.data))
      .catch((err) => console.error("Error loading orders:", err));
  }, []);

  return (
    <div style={{ padding: "20px" }}>
      <h2>ðŸ“¦ Past Orders</h2>
      {orders.length === 0 ? (
        <p>No orders found.</p>
      ) : (
        orders.map((order) => (
          <div key={order.orderid} style={{ marginBottom: "20px", borderBottom: "1px solid #ccc", paddingBottom: "10px" }}>
            <strong>Order #{order.orderid}</strong> â€” ${parseFloat(order.ordertotal).toFixed(2)}<br />
            Customer: {order.customername}<br />
            Time: {new Date(order.ordertimestamp).toLocaleString()}<br />
            Payment: {order.paymentmethod}<br />
            <ul>
              {order.items.map((item, idx) => (
                <li key={idx}>
                  Item ID: {item.menu_item_id}, Qty: {item.quantity}, ${parseFloat(item.price).toFixed(2)} â€” {item.transactioncategory}
                </li>
              ))}
            </ul>
          </div>
        ))
      )}
    </div>
  );
};

export default OrdersPage;
