// src/components/DrinkMenu.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom"; // ✅ 导入 useNavigate

const DrinkMenu = () => {
  const [drinkName, setDrinkName] = useState("");
  const [category, setCategory] = useState("");
  const [price, setPrice] = useState("");
  const [menuList, setMenuList] = useState([]);
  const navigate = useNavigate(); // ✅ 初始化 navigate

  const viewMenu = async () => {
    try {
      const res = await axios.get("http://localhost:5050/api/menu");
      setMenuList(res.data);
    } catch (err) {
      alert("❌ Failed to fetch menu.");
    }
  };

  const addDrink = async () => {
    if (!drinkName || !category || !price) {
      alert("Please fill out all fields.");
      return;
    }
    try {
      await axios.post("http://localhost:5050/api/menu/add", {
        itemname: drinkName,
        itemtype: category,
        itemprice: parseFloat(price),
      });
      alert("✅ Drink added successfully!");
      setDrinkName("");
      setCategory("");
      setPrice("");
      viewMenu();
    } catch (err) {
      alert("❌ Failed to add drink.");
    }
  };

  const deleteDrink = async () => {
    if (!drinkName) {
      alert("Please enter the drink name to delete.");
      return;
    }
    try {
      await axios.post("http://localhost:5050/api/menu/delete", {
        itemname: drinkName,
      });
      alert("🗑️ Drink deleted successfully!");
      viewMenu();
    } catch (err) {
      alert("❌ Failed to delete drink.");
    }
  };

  useEffect(() => {
    viewMenu();
  }, []);

  const handleBack = () => {
    navigate(-1); // 或者 navigate("/manager") 跳回 Manager 页面
  };

  return (
    <div style={{ padding: "20px" }}>
      <button onClick={handleBack} style={{ marginBottom: "10px" }}>
        🔙 Back
      </button>

      <h2>🥤 Drink Menu Management</h2>

      <div style={{ marginBottom: "20px" }}>
        <input
          type="text"
          placeholder="Drink Name"
          value={drinkName}
          onChange={(e) => setDrinkName(e.target.value)}
        />
        <input
          type="text"
          placeholder="Category"
          value={category}
          onChange={(e) => setCategory(e.target.value)}
        />
        <input
          type="number"
          placeholder="Price"
          value={price}
          onChange={(e) => setPrice(e.target.value)}
        />
        <div style={{ marginTop: "10px" }}>
          <button onClick={addDrink}>➕ Add Drink</button>
          <button onClick={deleteDrink}>🗑️ Delete Drink</button>
          <button onClick={viewMenu}>🔄 Refresh Menu</button>
        </div>
      </div>

      <h3>📋 Current Menu</h3>
      {menuList.length === 0 ? (
        <p>No items available.</p>
      ) : (
        <table border="1" cellPadding="8" style={{ borderCollapse: "collapse", width: "100%" }}>
          <thead style={{ backgroundColor: "#f2f2f2" }}>
            <tr>
              <th>ID</th>
              <th>Drink Name</th>
              <th>Category</th>
              <th>Price ($)</th>
              <th>Available</th>
            </tr>
          </thead>
          <tbody>
            {menuList.map((item) => (
              <tr key={item.itemid}>
                <td>{item.itemid}</td>
                <td>{item.itemname}</td>
                <td>{item.itemtype}</td>
                <td>{parseFloat(item.itemprice).toFixed(2)}</td>
                <td>{item.available ? "Yes" : "No"}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default DrinkMenu;
