import React, { useState, useEffect } from "react";
import axios from "axios";

const Trends = () => {
  const [trendType, setTrendType] = useState("Daily Sales");
  const [timeRange, setTimeRange] = useState("Last 7 Days");
  const [salesData, setSalesData] = useState([]);
  const [labels, setLabels] = useState([]);

  useEffect(() => {
    fetchTrendData(trendType, timeRange);
  }, [trendType, timeRange]);

  const fetchTrendData = async (type, time) => {
    try {
      const response = await axios.get("http://localhost:5050/api/trends", {
        params: {
          type,
          time
        }
      });
      setSalesData(response.data.values);
      setLabels(response.data.labels);
    } catch (err) {
      console.error("Error fetching trend data:", err);
      alert("Failed to load trends");
    }
  };

  const trendOptions = ["Daily Sales", "Peak Days", "Total Orders", "Product Usage"];
  const timeOptions = ["Last 7 Days", "Last 30 Days", "All Time"];

  return (
    <div style={{ padding: "20px" }}>
      <h2>ðŸ“ˆ Trends Report</h2>
      <div style={{ marginBottom: "10px" }}>
        <label>Trend Type: </label>
        <select value={trendType} onChange={(e) => setTrendType(e.target.value)}>
          {trendOptions.map((opt) => (
            <option key={opt} value={opt}>{opt}</option>
          ))}
        </select>

        <label style={{ marginLeft: "20px" }}>Time Range: </label>
        <select value={timeRange} onChange={(e) => setTimeRange(e.target.value)}>
          {timeOptions.map((opt) => (
            <option key={opt} value={opt}>{opt}</option>
          ))}
        </select>
      </div>

      <div style={{ display: "flex" }}>
        <div style={{ width: "300px" }}>
          <h3>{trendType}</h3>
          <ul>
            {labels.map((label, idx) => (
              <li key={idx}>{label}: {salesData[idx]}</li>
            ))}
          </ul>
        </div>

        <div style={{ flexGrow: 1, marginLeft: "30px" }}>
          {/* You can integrate a charting library like Chart.js or Recharts here */}
          <p>(Graph Placeholder)</p>
        </div>
      </div>
    </div>
  );
};

export default Trends;
