import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Inventory = () => {
  const [itemId, setItemId] = useState("");
  const [quantity, setQuantity] = useState("");
  const [inventoryList, setInventoryList] = useState([]);
  const navigate = useNavigate();

  // 获取库存数据
  useEffect(() => {
    viewInventory();
  }, []);

  const viewInventory = async () => {
    try {
      const res = await axios.get("http://localhost:5050/api/inventory");
      setInventoryList(res.data);
    } catch (err) {
      alert("❌ Failed to fetch inventory");
    }
  };

  const addInventory = async () => {
    if (!itemId || !quantity) {
      alert("Please fill in both fields.");
      return;
    }
    try {
      const result = await axios.post("http://localhost:5050/api/inventory/add", {
        menuitemid: itemId,
        quantity: quantity,
      });
      alert("✅ Inventory added successfully!");
      setItemId("");
      setQuantity("");
      viewInventory();
    } catch (err) {
      alert("❌ Failed to add inventory.");
    }
  };

  const updateInventory = async () => {
    if (!itemId || !quantity) {
      alert("Please fill in both fields.");
      return;
    }
    try {
      await axios.post("http://localhost:5050/api/inventory/update", {
        inventoryid: itemId,
        quantity: quantity,
      });
      alert("✅ Inventory updated successfully!");
      viewInventory();
    } catch (err) {
      alert("❌ Failed to update inventory.");
    }
  };

  const deleteInventory = async () => {
    if (!itemId) {
      alert("Please enter an Item ID to delete.");
      return;
    }
    try {
      await axios.delete("http://localhost:5050/api/inventory/delete", {
        data: { inventoryid: itemId },
      });
      alert("✅ Inventory deleted successfully!");
      viewInventory();
    } catch (err) {
      alert("❌ Failed to delete inventory.");
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>📦 Inventory Management</h2>

      <div>
        <input
          type="text"
          placeholder="Item ID"
          value={itemId}
          onChange={(e) => setItemId(e.target.value)}
        />
        <input
          type="number"
          placeholder="Quantity"
          value={quantity}
          onChange={(e) => setQuantity(e.target.value)}
        />
        <div>
          <button onClick={addInventory}>➕ Add Inventory</button>
          <button onClick={updateInventory}>🔄 Update Inventory</button>
          <button onClick={deleteInventory}>❌ Delete Inventory</button>
        </div>
      </div>

      <div style={{ marginTop: "20px" }}>
        <h3>Current Inventory:</h3>
        <table border="1" style={{ width: "100%" }}>
          <thead>
            <tr>
              <th>Inventory ID</th>
              <th>Item ID</th>
              <th>Quantity</th>
            </tr>
          </thead>
          <tbody>
            {inventoryList.map((item) => (
              <tr key={item.inventoryid}>
                <td>{item.inventoryid}</td>
                <td>{item.menuitemid}</td>
                <td>{item.quantity}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <button style={{ marginTop: "20px" }} onClick={() => navigate("/")}>
        ⬅️ Back
      </button>
    </div>
  );
};

export default Inventory;
