// Employee.js (Web version of ShowEmployee)
import React, { useState } from 'react';
import axios from 'axios';

const Employee = () => {
  const [employeeList, setEmployeeList] = useState([]);
  const [formData, setFormData] = useState({
    employeeid: '',
    employeename: '',
    employeepassword: '',
    employeetype: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const fetchEmployees = async () => {
    try {
      const res = await axios.get('/api/employees');
      setEmployeeList(res.data);
    } catch (err) {
      alert('Failed to load employees.');
    }
  };

  const addEmployee = async () => {
    const { employeename, employeepassword, employeetype } = formData;
    if (!employeename || !employeepassword || !employeetype) return alert('Fill all fields.');
    try {
      await axios.post('/api/employees', formData);
      alert('Employee added.');
      fetchEmployees();
    } catch (err) {
      alert('Failed to add employee.');
    }
  };

  const updateEmployee = async () => {
    try {
      await axios.put(`/api/employees/${formData.employeeid}`, formData);
      alert('Employee updated.');
      fetchEmployees();
    } catch (err) {
      alert('Failed to update employee.');
    }
  };

  const deleteEmployee = async () => {
    try {
      await axios.delete(`/api/employees/${formData.employeeid}`);
      alert('Employee deleted.');
      fetchEmployees();
    } catch (err) {
      alert('Failed to delete employee.');
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>ðŸ‘¥ Employee Management</h2>
      <input placeholder="ID (for update/delete)" name="employeeid" value={formData.employeeid} onChange={handleChange} /><br />
      <input placeholder="Name" name="employeename" value={formData.employeename} onChange={handleChange} /><br />
      <input placeholder="Password" name="employeepassword" value={formData.employeepassword} onChange={handleChange} /><br />
      <input placeholder="Role" name="employeetype" value={formData.employeetype} onChange={handleChange} /><br />

      <button onClick={addEmployee}>Add</button>
      <button onClick={updateEmployee}>Update</button>
      <button onClick={deleteEmployee}>Delete</button>
      <button onClick={fetchEmployees}>View All</button>

      <ul>
        {employeeList.map(emp => (
          <li key={emp.employeeid}>{emp.employeeid} - {emp.employeename} ({emp.employeetype})</li>
        ))}
      </ul>
    </div>
  );
};

export default Employee;
