import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Inventory = () => {
  const [ingredients, setIngredients] = useState([]);
  const [newIngredient, setNewIngredient] = useState({ itemname: "", quantity: "" });
  const navigate = useNavigate();

  // Ëé∑ÂèñÂ∫ìÂ≠òÊï∞ÊçÆ
  useEffect(() => {
    fetchIngredients();
  }, []);

  const fetchIngredients = async () => {
    try {
      const res = await axios.get("http://localhost:5050/api/inventory");
      setIngredients(res.data);
    } catch (err) {
      console.error("Error fetching inventory:", err);
    }
  };

  const handleAdd = async () => {
    if (!newIngredient.itemname || !newIngredient.quantity) {
      alert("Please enter both item name and quantity.");
      return;
    }
    try {
      // Ê†πÊçÆÂ∫ìÂ≠òÂêçÁß∞ÂíåÊï∞ÈáèÊ∑ªÂä†Â∫ìÂ≠ò
      const menuItem = await axios.get(`http://localhost:5050/api/menu`);
      const menuItemId = menuItem.data.find((item) => item.itemname === newIngredient.itemname)?.itemid;

      if (!menuItemId) {
        alert("Item name not found.");
        return;
      }

      await axios.post("http://localhost:5050/api/inventory/add", {
        menuitemid: menuItemId,
        quantity: newIngredient.quantity,
      });
      setNewIngredient({ itemname: "", quantity: "" });
      fetchIngredients();
    } catch (err) {
      console.error("Error adding ingredient:", err);
    }
  };

  const handleDelete = async (inventoryid) => {
    try {
      await axios.delete("http://localhost:5050/api/inventory/delete", {
        data: { inventoryid },
      });
      fetchIngredients();
    } catch (err) {
      console.error("Error deleting ingredient:", err);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>üì¶ Inventory Management</h2>

      <div>
        <input
          type="text"
          placeholder="Item Name"
          value={newIngredient.itemname}
          onChange={(e) => setNewIngredient({ ...newIngredient, itemname: e.target.value })}
        />
        <input
          type="number"
          placeholder="Quantity"
          value={newIngredient.quantity}
          onChange={(e) => setNewIngredient({ ...newIngredient, quantity: e.target.value })}
        />
        <button onClick={handleAdd}>‚ûï Add Ingredient</button>
      </div>

      <ul style={{ listStyle: "none", padding: 0 }}>
        {ingredients.map((item, idx) => (
          <li key={idx} style={{ marginTop: "10px" }}>
            <strong>{item.itemname}</strong> ‚Äî {item.quantity}
            <button onClick={() => handleDelete(item.inventoryid)} style={{ marginLeft: "10px" }}>‚ùå Delete</button>
          </li>
        ))}
      </ul>

      <button style={{ marginTop: "20px" }} onClick={() => navigate("/")}>‚¨ÖÔ∏è Back</button>
    </div>
  );
};

export default Inventory;
