import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const SeasonalDrinkMenu = () => {
  const [drinkName, setDrinkName] = useState("");
  const [category] = useState("Seasonal"); // Á°Æ‰øùÈªòËÆ§ÊòØSeasonal
  const [price, setPrice] = useState("");
  const [menuList, setMenuList] = useState([]);
  const navigate = useNavigate();

  // Ëé∑ÂèñSeasonalËèúÂçï
  const viewMenu = async () => {
    try {
      const res = await axios.get("http://localhost:5050/api/menu", {
        params: {
          itemtype: "Seasonal", // Á°Æ‰øùÂè™Ëé∑ÂèñSeasonalÁ±ªÂûãÁöÑÈ•ÆÊñô
        }
      });
      setMenuList(res.data);
    } catch (err) {
      alert("‚ùå Failed to fetch seasonal menu.");
    }
  };

  // Ê∑ªÂä†Â≠£ËäÇÊÄßÈ•ÆÊñô
  const addDrink = async () => {
    if (!drinkName || !category || !price) {
      alert("Please fill out all fields.");
      return;
    }
    try {
      await axios.post("http://localhost:5050/api/menu/seasonal", {
        itemname: drinkName,
        itemtype: category,
        itemprice: parseFloat(price),
      });
      alert("‚úÖ Seasonal drink added successfully!");
      setDrinkName("");
      setPrice("");
      viewMenu();
    } catch (err) {
      alert("‚ùå Failed to add seasonal drink.");
    }
  };

  // Âà†Èô§Â≠£ËäÇÊÄßÈ•ÆÊñô
  const deleteDrink = async () => {
    if (!drinkName) {
      alert("Please enter the drink name to delete.");
      return;
    }
    try {
      await axios.post("http://localhost:5050/api/menu/delete", {
        itemname: drinkName,
      });
      alert("üóëÔ∏è Seasonal drink deleted successfully!");
      viewMenu();
    } catch (err) {
      alert("‚ùå Failed to delete seasonal drink.");
    }
  };

  // Êõ¥Êñ∞Â≠£ËäÇÊÄßÈ•ÆÊñô
  const updateDrink = async () => {
    if (!drinkName || !category || !price) {
      alert("Please fill out all fields.");
      return;
    }
    try {
      await axios.post("http://localhost:5050/api/menu/update", {
        itemname: drinkName,
        itemtype: category,
        itemprice: parseFloat(price),
      });
      alert("‚úÖ Seasonal drink updated successfully!");
      viewMenu();
    } catch (err) {
      alert("‚ùå Failed to update seasonal drink.");
    }
  };

  useEffect(() => {
    viewMenu();
  }, []);

  // ËøîÂõûÊåâÈíÆ
  const handleBack = () => {
    navigate(-1); // ÊàñËÄÖ navigate("/manager") Ë∑≥Âõû Manager È°µÈù¢
  };

  return (
    <div style={{ padding: "20px" }}>
      <button onClick={handleBack} style={{ marginBottom: "10px" }}>
        üîô Back
      </button>

      <h2>ü•§ Seasonal Drink Menu Management</h2>

      <div style={{ marginBottom: "20px" }}>
        <input
          type="text"
          placeholder="Drink Name"
          value={drinkName}
          onChange={(e) => setDrinkName(e.target.value)}
        />
        <input
          type="number"
          placeholder="Price"
          value={price}
          onChange={(e) => setPrice(e.target.value)}
        />
        <div style={{ marginTop: "10px" }}>
          <button onClick={addDrink}>‚ûï Add Seasonal Drink</button>
          <button onClick={deleteDrink}>üóëÔ∏è Delete Seasonal Drink</button>
          <button onClick={updateDrink}>üîÑ Update Seasonal Drink</button>
        </div>
      </div>

      <h3>üìã Current Seasonal Drinks</h3>
      {menuList.length === 0 ? (
        <p>No seasonal drinks available.</p>
      ) : (
        <table border="1" cellPadding="8" style={{ borderCollapse: "collapse", width: "100%" }}>
          <thead style={{ backgroundColor: "#f2f2f2" }}>
            <tr>
              <th>ID</th>
              <th>Drink Name</th>
              <th>Category</th>
              <th>Price ($)</th>
              <th>Available</th>
            </tr>
          </thead>
          <tbody>
            {menuList.map((item) => (
              <tr key={item.itemid}>
                <td>{item.itemid}</td>
                <td>{item.itemname}</td>
                <td>{item.itemtype}</td>
                <td>{parseFloat(item.itemprice).toFixed(2)}</td>
                <td>{item.available ? "Yes" : "No"}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default SeasonalDrinkMenu;
