import React, { useEffect, useState } from "react";
import axios from "axios";

const Inventory = () => {
  const [ingredients, setIngredients] = useState([]);
  const [newIngredient, setNewIngredient] = useState({ menuitemid: "", quantity: "" });

  // Fetch inventory data
  useEffect(() => {
    fetchIngredients();
  }, []);

  const fetchIngredients = async () => {
    try {
      const res = await axios.get("http://localhost:5050/api/inventory");
      setIngredients(res.data);
    } catch (err) {
      console.error("Error fetching inventory:", err);
    }
  };

  const handleAdd = async () => {
    if (!newIngredient.menuitemid || !newIngredient.quantity) {
      alert("Please enter both item ID and quantity.");
      return;
    }
    try {
      await axios.post("http://localhost:5050/api/inventory/add", newIngredient);
      setNewIngredient({ menuitemid: "", quantity: "" });
      fetchIngredients();
    } catch (err) {
      console.error("Error adding ingredient:", err);
    }
  };

  const handleDelete = async (inventoryid) => {
    try {
      await axios.delete("http://localhost:5050/api/inventory/delete", {
        data: { inventoryid },
      });
      fetchIngredients();
    } catch (err) {
      console.error("Error deleting ingredient:", err);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>üì¶ Inventory Management</h2>

      <div>
        <input
          type="number"
          placeholder="Menu Item ID"
          value={newIngredient.menuitemid}
          onChange={(e) => setNewIngredient({ ...newIngredient, menuitemid: e.target.value })}
        />
        <input
          type="number"
          placeholder="Quantity"
          value={newIngredient.quantity}
          onChange={(e) => setNewIngredient({ ...newIngredient, quantity: e.target.value })}
        />
        <button onClick={handleAdd}>‚ûï Add Ingredient</button>
      </div>

      <ul style={{ listStyle: "none", padding: 0 }}>
        {ingredients.map((item, idx) => (
          <li key={idx} style={{ marginTop: "10px" }}>
            <strong>{item.itemname}</strong> ‚Äî {item.quantity}
            <button onClick={() => handleDelete(item.inventoryid)} style={{ marginLeft: "10px" }}>‚ùå Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Inventory;
